mapper.select:
SELECT U.USER_ID, U.USERNAME, U.PASSWORD, U.PERSON_NAME, U.RIGHTS, U.ROLE_ID, U.STATUS, U.SKIN, U.EMAIL, U.USER_NO, U.PHONE, U.PROVINCE_ID, U.MUNICIPALITY_ID, U.COUNTY_ID, U.DEAIL_ADDRESS, U.IMG_PATH, U.IS_ONLINE, U.DEPT_ID, U.BIRTHDAY, U.SCHOOL, U.REMARKS, P.PROVINCE_NAME, M.MUNICIPALITY_NAME, C.COUNTY_NAME FROM SYS_USER U LEFT JOIN DB_PROVINCE P ON U.PROVINCE_ID = P.PROVINCE_ID LEFT JOIN DB_MUNICIPALITY M ON U.MUNICIPALITY_ID = M.MUNICIPALITY_ID LEFT JOIN DB_COUNTY C ON U.COUNTY_ID = C.COUNTY_ID where 1=1
and USERNAME = #{username}
and USER_ID = #{ userId }

SELECT U.USER_ID, U.USERNAME, U.PASSWORD, U.PERSON_NAME, U.RIGHTS, U.ROLE_ID, U.STATUS, U.SKIN, U.EMAIL, U.USER_NO, U.PHONE, U.PROVINCE_ID, U.MUNICIPALITY_ID, U.COUNTY_ID, U.DEAIL_ADDRESS, U.IMG_PATH, U.IS_ONLINE, U.DEPT_ID, U.BIRTHDAY, U.SCHOOL, U.REMARKS, P.PROVINCE_NAME, M.MUNICIPALITY_NAME, C.COUNTY_NAME FROM SYS_USER U LEFT JOIN DB_PROVINCE P ON U.PROVINCE_ID = P.PROVINCE_ID LEFT JOIN DB_MUNICIPALITY M ON U.MUNICIPALITY_ID = M.MUNICIPALITY_ID LEFT JOIN DB_COUNTY C ON U.COUNTY_ID = C.COUNTY_ID where 1=1
and (USERNAME like '%${username}%' || PERSON_NAME like '%${username}%')
${column.name} ${column.orderDir}

mapper.insert:
select UUID()
insert into SYS_USER (
) values ( #{ userId,jdbcType=VARCHAR }, #{ username,jdbcType=VARCHAR }, #{ password,jdbcType=VARCHAR }, #{ personName,jdbcType=VARCHAR }, #{ rights,jdbcType=VARCHAR }, #{ roleId,jdbcType=VARCHAR }, #{ status,jdbcType=VARCHAR }, #{ skin,jdbcType=VARCHAR }, #{ email,jdbcType=VARCHAR }, #{ userNo,jdbcType=VARCHAR }, #{ phone,jdbcType=VARCHAR }, #{ provinceId,jdbcType=VARCHAR }, #{ municipalityId,jdbcType=VARCHAR }, #{ countyId,jdbcType=VARCHAR }, #{ deailAddress,jdbcType=VARCHAR }, #{ imgPath,jdbcType=VARCHAR }, #{ isOnline,jdbcType=VARCHAR }, #{ deptId,jdbcType=VARCHAR }, #{ birthday,jdbcType=DATE }, #{ school,jdbcType=VARCHAR }, #{ remarks,jdbcType=VARCHAR } )
